import React from "react";
import {
    Drawer,
    ListItem,
    ListItemButton,
    ListItemIcon,
    ListItemText,
    List,
    Divider,
    IconButton,
    useTheme,
  } from "@mui/material";
//   import { Link, useLocation } from "react-router-dom";
//   import HomeIcon from "@mui/icons-material/Home";
//   import HowToRegIcon from "@mui/icons-material/HowToReg";
//   import LoginIcon from "@mui/icons-material/Login";
//   import Brightness7Icon from "@mui/icons-material/Brightness7";
//   import Brightness4Icon from "@mui/icons-material/Brightness4";
//   import { conditions } from "./ReduxServices/Slices/conditions-slice";
//   import { useSelector, useDispatch } from "react-redux";
//   import SettingsIcon from "@mui/icons-material/Settings";
//   import ArticleIcon from "@mui/icons-material/Article";
//   import LogoutIcon from "@mui/icons-material/Logout";
//   import {  useNavigate, } from "react-router-dom";

export default function SideBar(){
    // const pages = ["Home", "Sign In", "sign Up"];
    // const logInPages = ["Home", "New Post", "Settings"];
    // const pagesHref = ["/", "/signin", "/signup"];
    // const conditionsPage = useSelector((state) => state.condition);
    // const arrayIcons = [<HomeIcon />, <LoginIcon />, <HowToRegIcon />];
    // const arrayIconsLogin = [<HomeIcon />, <ArticleIcon />, <SettingsIcon />];
    // const location = useLocation();
    // const theme = useTheme();
    // const dispatch = useDispatch();
    // const user = JSON.parse(localStorage.getItem("user-info"));
    // const logInPagesHref = ["/", "/newPost", `/settings`];
  
    // useEffect(() => {
    //   const user = JSON.parse(localStorage.getItem("user-info"));
    //   if (user) {
    //     dispatch(conditions(true));
    //   }
    // }, []);
    return(
         <Drawer
        variant="temporary"
        open={props.open}
        onClose={props.openToggle}
        sx={{ width: "240px", display: { xs: "flex", md: "none" } }}
      >
        <List sx={{ width: "240px", flexDirection: "column" }}>
          <IconButton
            onClick={() => {
              localStorage.setItem(
                "currentMode",
                theme.palette.mode === "light" ? "dark" : "light"
              );
              props.darkMode();
            }}
          >
            {props.mode === "light" ? (
              <Brightness4Icon />
            ) : (
              <Brightness7Icon sx={{ color: "orange" }} />
            )}
          </IconButton>
          <Divider />
          {conditionsPage
            ? logInPages.map((page, index) => (
                <Link
                  to={logInPagesHref[index]}
                  style={{ textDecoration: "none" }}
                  key={page}
                >
                  <ListItem
                    disablePadding
                    sx={{
                      backgroundColor:
                        location.pathname === logInPagesHref[index]
                          ? "#f8f8f8"
                          : "",
                    }}
                  >
                    <ListItemButton>
                      <ListItemIcon>{arrayIconsLogin[index]}</ListItemIcon>
                      <ListItemText primary={page} />
                    </ListItemButton>
                  </ListItem>
                </Link>
              ))
            : pages.map((page, index) => (
                <Link
                  to={pagesHref[index]}
                  style={{ textDecoration: "none" }}
                  key={page}
                >
                  <ListItem
                    disablePadding
                    sx={{
                      backgroundColor:
                        location.pathname === pagesHref[index] ? "#f8f8f8" : "",
                    }}
                  >
                    <ListItemButton>
                      <ListItemIcon>{arrayIcons[index]}</ListItemIcon>
                      <ListItemText primary={page} />
                    </ListItemButton>
                  </ListItem>
                </Link>
              ))}
          {conditionsPage ? (
            <>
              <IconButton
                onClick={() => {
                  dispatch(conditions(false));
                  console.log("condition set to false");
                  localStorage.removeItem("user-info");
                  localStorage.removeItem("condition");
                }}
              >
                <LogoutIcon />
              </IconButton>
            </>
          ) : (
            ""
          )}
        </List>
      </Drawer>
    )
    
}
