import { Button, Typography, useTheme } from "@mui/material";
import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import Tags from "../components/tags/Tags";
import Articles from "../components/articles/Articles";
import { getTagFetch } from "../ReduxPages/ReduxSlices/getArticlesByTag-slice";

export default function Home() {
  const theme = useTheme();
  const [tag, setTags] = useState('')
  const condition = useSelector((state) => state.condition);
  const getArticleByTag = useSelector((state) => state.getArticleByTag);
  const dispatch = useDispatch();
  const [apppear, setAppear] = useState(false);
  
//   useEffect(()=>{

//   },[dispatch])
  
  function tagsFilter(tag) {
    dispatch(getTagFetch(tag));
    setAppear(true);
    setTags(tag)
    console.log(apppear);
    console.log(getArticleByTag.articles);
  }

  useEffect(()=>{
    setAppear
  },[])


  function clickHandeler(){
    setAppear(false); 
  }

  return (
    <>
      {condition ? (
        ""
      ) : (
        <>
          <Typography
            component={"div"}
            sx={{
              width: "100%",
              height: "170px",
              backgroundColor: theme.palette.mainColor.main,
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
              flexDirection: "column",
            }}
            className="background"
          >
            <h2
              className="text-center"
              style={{ color: "#ffffff", fontSize: "30px", fontWeight: "bold" }}
            >
              conduit
            </h2>
            <p
              className="text-center"
              style={{ color: "#ffffff", fontSize: "20px" }}
            >
              {" "}
              A place to share Knowledge{" "}
            </p>
          </Typography>

          <div className="container mt-5">
            <div className="row">
              <div className="col-md-8">
                <Button
                  className="border-bottom parabottom"
                  style={{
                    color: theme.palette.mainColor.main,
                    position: "relative",
                  }}
                  onClick={clickHandeler}
                >
                  Global Article   
                </Button>
                {apppear ? <span style={{color: theme.palette.mainColor.main}}>
                {`#${tag}`}
                </span> :
                ''}
                <Articles apppear={apppear} getArticleByTag={getArticleByTag} />
              </div>
              
              <div className="col-md-4">
                <Tags tagsFilter={tagsFilter} />
              </div>
            </div>
          </div>
        </>
      )}
    </>
  );
}
