import React, { useEffect } from "react";
import Brightness7Icon from "@mui/icons-material/Brightness7";
import Brightness4Icon from "@mui/icons-material/Brightness4";
import {
  AppBar,
  Container,
  Toolbar,
  Typography,
  IconButton,
  Box,
  useTheme,
} from "@mui/material";
import MenuIcon from "@mui/icons-material/Menu";
import { Link, Outlet, useLocation, useNavigate } from "react-router-dom";
import { condition } from "../../ReduxPages/ReduxSlices/condition-slice";
export default function AppBarR(props) {
  const pages = ["Home", "Sign In", "sign Up"];
  const pagesHref = ["/", "/signin", "/signup"];
  const location = useLocation();
  const theme = useTheme();
  //   const logInPages = ["Home", "New Post", "Settings"];
  //   const logInPagesHref = ["/", "/newPost", `/settings`  ];
  const navigate = useNavigate();
  return (
    <AppBar
      position="static"
      sx={{ backgroundColor: "transparent", boxShadow: "none" }}
    >
      <Container>
        <Toolbar disableGutters>
          <IconButton
            aria-label=""
            sx={{ display: { xs: "flex", md: "none" } }}
            onClick={() => {
              props.openToggle();
              localStorage.setItem("current value", props.mode);
            }}
          >
            <MenuIcon />
          </IconButton>
          <Box sx={{ flexGrow: 1 }}>
            <Link
              to="/"
              style={{
                color: theme.palette.mainColor.main,
                textDecoration: "none",
                fontSize: "25px",
              }}
            >
              Conduit
            </Link>
          </Box>
          <Box
            sx={{
              display: { xs: "none", md: "flex" },
              justifyContent: "center",
              alignItems: "center",
              flexDirection: "row",
            }}
          >
            <IconButton
              onClick={() => {
                props.darkMode();
              }}
            >
              {theme.palette.mode === "dark" ? (
                <Brightness7Icon sx={{ color: "orange" }} />
              ) : (
                <Brightness4Icon />
              )}
            </IconButton>
            {pages.map((page, index) => (
              <Link
                to={pagesHref[index]}
                style={{
                  textDecoration: "none",
                  color: theme.palette.mainColor.main,
                  backgroundColor:
                    location.pathname === pagesHref[index] ? theme.palette.mainColor.background : "",
                }}
                key={page}
              >
                <Typography
                  className="btn"
                  component={"h2"}
                  variant="h6"
                  sx={{
                    fontSize: "16px",
                    p: "10px",
                    color:theme.palette.mainColor.text,
                  }}
                >
                  {page}
                </Typography>
              </Link>
            ))}
          </Box>
        </Toolbar>
      </Container>
      <Outlet />
    </AppBar>
  );
}
