import { TextField, useTheme,Button } from "@mui/material";
import React, { useState } from "react";

export default function Settings() {
  const theme = useTheme();
  const user = JSON.parse(localStorage.getItem("user-info"));
  const [image, setImage] = useState("");
  const [username, setUsername] = useState("");

  return (
    <div className="container">
      <h2
        className="text-center mt-4"
        style={{ color: theme.palette.mainColor.main }}
      >
        Your Settings
      </h2>
      <div className="d-flex flex-column justify-content-center align-items-center">
        <div style={{width:"70%", height:'80px'}}>
          <label for="image" className="mb-2" style={{color:theme.palette.mainColor.text}}> Image </label>
          <TextField id="image" value={user.image} sx={{width:'100%'}}/>
        </div>
        <div style={{width:"70%", height:'80px'}}>
          <label for="username" className="mb-2" style={{color:theme.palette.mainColor.text}}> Username </label>
          <TextField type="text" id="username" value={user.username} sx={{width:'100%'}}/>
        </div>
        <div style={{width:"70%", height:'80px'}}>
          <label for="bio"> Bio </label>
          <TextField type="text" id="bio" multiline rows={5} value={user.bio} sx={{width:'100%'}}/>
        <div style={{height:'80px'}}>
          <label for="email" className="mb-2" style={{color:theme.palette.mainColor.text}}> Email </label>
          <TextField id="email" type="email" value={user.email} sx={{width:'100%'}}/>
        </div>
        <div style={{height:'80px'}}>
          <label for="password" className="mb-2 mt-3" style={{color:theme.palette.mainColor.text}}> Password </label>
          <TextField id="password" type="password" placeholder="New Password" value={user.password} sx={{width:'100%'}}/>
        </div>
        <Button
            // type="submit"
            // onClick={(e)=>{clickHandeler(e)}}
            sx={{
              mt: "20px",
              width: "200px",
              color: theme.palette.mainColor.text,
              backgroundColor: theme.palette.mainColor.main,
              "&:hover": {
                backgroundColor: "#ffffff",
                color: theme.palette.mainColor.main,
              },
            }}
          >
            update settings
          </Button>   
        </div>
      </div>
    </div>
  );
}
