import {
  Avatar,
  Card,
  CardHeader,
  CardContent,
  Typography,
  IconButton,
  Button,
  useTheme,
} from "@mui/material";
import FavoriteIcon from "@mui/icons-material/Favorite";
import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { articleFetch } from "../../ReduxPages/ReduxSlices/getArticles-slice";
import { Link } from "react-router-dom";

export default function Articles() {
  const theme = useTheme();
  const articles = useSelector((state) => state.article);
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(articleFetch());
  }, []);
  if (!Array.isArray(articles.articles)) {
    return <p></p>;
  }

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toISOString().slice(0, 10);
  };

  let articleMapping = articles.articles.map((article) => (
    <Card
      elevation={3}
      key={article.slug}
      sx={{ position: "relative", pb: "20px", mb: "20px" }}
    >
      <Link
        to={`/profile/${article.author.username}`}
        style={{ color: theme.palette.mainColor.main, textDecoration: "none" }}
      >
        <CardHeader
          avatar={<Avatar src={article.author.image} alt="m" />}
          title={article.author.username}
          subheader={formatDate(article.createdAt)}
        />
      </Link>
      <Link to={`/${article.slug}`} style={{ color: theme.palette.mainColor.main, textDecoration: "none" }}>
      <CardContent>
        <Typography component={"p"}>{article.title}</Typography>
        <Typography component={"p"} color="text.secondary">
          {article.body}
        </Typography>
      </CardContent>
      <div className="text-end me-5">
        {article.tagList.map((tag) => (
          <Button
            className="btn rounded-pill ms-2"
            sx={{
              color: theme.palette.mainColor.text,
              backgroundColor: theme.palette.mainColor.background,
            }}
            key={tag}
          >
            {tag}
          </Button>
        ))}
      </div>
      <IconButton
        aria-label="add to favorites"
        sx={{ position: "absolute", top: "10px", right: "10px" }}
      >
        <span style={{ fontSize: "16px" }}>{article.favoritesCount}</span>
        <FavoriteIcon />
      </IconButton>
      </Link>
    </Card>
  ));
  return <>{articleMapping}</>;
}
