import { TextField, Typography, useTheme, Button } from "@mui/material";
import React,{useState} from "react";
import { useNavigate } from "react-router";

export default function NewPost() {
  const navigate = useNavigate();
  const [tags, setTags] = useState([]); 
  const theme = useTheme();


  function clickHandeler(){
     console.log(tags)
  }
  const handleTagChange = (e) => {
    const inputTags = e.target.value.split(" ");
    setTags(inputTags);
  };

  return (
    <div className="container">
      <div className="d-flex justify-content-center align-items-center mt-2 flex-column">
        <Typography
          component={"h2"}
          sx={{
            color: theme.palette.mainColor.main,
            fontSize: "30px",
            mt: "30px",
          }}
        >
          Add An Article
        </Typography>
        <div style={{ height: "80px", width: "70%" }}>
          <TextField
            variant="outlined"
            label="Article Title"
            type="text"
            id="title"
            sx={{ mt: "30px", width: "100%" }}
          />
        </div>
        <div style={{ height: "80px", width: "70%" }}>
          <TextField
            variant="outlined"
            label="description"
            type="text"
            id="description"
            sx={{ mt: "30px", width: "100%" }}
          />
        </div>
        <div style={{ height: "80px", width: "70%" }}>
          <TextField
            id="body"
            label="body"
            multiline
            rows={6}
            variant="outlined"
            sx={{ mt: "30px", width: "100%" }}
          />

          <TextField
            id="Tags"
            label="Tags"
            variant="outlined"
            sx={{ mt: "30px", width: "100%" }}
            onChange={(e)=>setTagse.target.value.split(" ")}
          />
          <Button
            onClick={()=>{clickHandeler()}}
            sx={{
              mt: "20px",
              width: "150px",
              color: theme.palette.mainColor.text,
              backgroundColor: theme.palette.mainColor.main,
              "&:hover": {
                backgroundColor: "#ffffff",
                color: theme.palette.mainColor.main,
              },
            }}
          >
            Post
          </Button>
        </div>
      </div>
    </div>
  );
}
