import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { Link, useNavigate } from "react-router-dom";
import { Box, Button, Container, TextField, Typography } from "@mui/material";
import { signUpFetch } from "../ReduxPages/ReduxSlices/signup-slice";
import { Formik, Field, Form } from "formik";
import * as Yup from "yup";

export default function SignUp() {
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const validationSchema = Yup.object().shape({
    username: Yup.string()
      .required("Username is required")
      .min(4, "Username must be at least 4 characters")
      .max(20, "Username must be at most 20 characters")
      .matches(
        /^[a-zA-Z][a-zA-Z0-9-_]*$/,
        "username can contain alphanumeric characters, hyphens, and underscores"
      ),
    email: Yup.string().email("Invalid email").required("Email is required"),
    password: Yup.string()
      .required("Password is required")
      .min(8, "Password must be at least 8 characters")
      .max(20, "Password must be at most 20 characters")
      .matches(
        /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%]).{8,24}$/,
        "Password must contain at least one uppercase letter, one lowercase letter"
      ),
  });

  const initialValues = {
    username: "",
    email: "",
    password: "",
  };

  async function clickHandler(values) {
    await dispatch(signUpFetch({ values }));
    // navigate("/");
  }
  return (
    <Formik
      initialValues={initialValues}
      validationSchema={validationSchema}
      onSubmit={clickHandler}
    >
      {({
        values,
        errors,
        touched,
        handleChange,
        handleBlur,
        handleSubmit,
        isSubmitting,
        /* and other goodies */
      }) => (
        <Form className="container d-flex flex-column w-50 mt-4 " onSubmit={handleSubmit}>
          <div className="mb-4">

          </div>
          <Field
            as={TextField}
            type="text"
            name="username"
            id="username"
            label="Username"
            onChange={handleChange}
            onBlur={handleBlur}
            value={values.username}
            error={touched.username && !!errors.username}
            helperText={touched.username && errors.username}
            className="mb-4 mt-4"
            
          />
          <div className="mb-4">

          <Field
            as={TextField}
            type="email"
            name="email"
            id="email"
            label="Email"
            onChange={handleChange}
            onBlur={handleBlur}
            value={values.email}
            error={touched.email && !!errors.email}
            helperText={touched.email && errors.email}
          />
          </div>
          <div className="mb-4">
          <Field
            as={TextField}
            type="password"
            name="password"
            id="password"
            label="Password"
            onChange={handleChange}
            onBlur={handleBlur}
            value={values.password}
            error={touched.password && !!errors.password}
            helperText={touched.password && errors.password}
          />
          </div>
          <Button
            sx={{
              color: "#5CB85C",
              width:"150px",
              borderColor: "#5CB85C",
              borderWidth: "1px",
              borderStyle: "solid",
              "&:hover": { color: "white", backgroundColor: "#5CB85C" },
            }}
            type="submit"
            disabled={isSubmitting}
          >
            Submit
          </Button>
        </Form>
      )}
    </Formik>
  );
}
