import { Avatar, IconButton, Typography, useTheme } from "@mui/material";
import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import SettingsIcon from "@mui/icons-material/Settings";
import { profileFetch } from "../ReduxPages/ReduxSlices/profile-slice";
import { useDispatch, useSelector } from "react-redux";
import AddIcon from "@mui/icons-material/Add";
import { followFetch } from "../ReduxPages/ReduxSlices/followUser-slice";
import DoneIcon from '@mui/icons-material/Done';

export default function Profile() {
  const theme = useTheme();
  const user = JSON.parse(localStorage.getItem("user-info"));
  const dispatch = useDispatch();
  const { username } = useParams();
  const profile = useSelector((state) => state.profile);
  const token = user.token;

  useEffect(() => {
    dispatch(profileFetch({ token, username }));
    console.log(profile);
  }, []);

  async function clickhandeler(){
     dispatch(followFetch({username ,token}));
    // const follow = res.payload; 
    // try{
    //   if(follow.errors){
    //     console.log('errors'); 
    //   } else{
    //     console.log('user followed')
    //   }
    // } catch(error){
    //   error.console('error occured', error); 
    // }
  }

  return (
    <div>
      <Typography
        component={"div"}
        className="background"
        sx={{
          width: "100%",
          height: "350px",
          backgroundColor: theme.palette.mainColor.main,
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          flexDirection: "column",
        }}
      >
        <Avatar
          sx={{ width: "100px", height: "100px" }}
          src={profile.image}
          alt=""
        />
        <Typography
          component={"h2"}
          sx={{
            color: theme.palette.mainColor.text,
            fontSize: "30px",
            textTransform: "capitalize",
            mt: "10px",
          }}
        >
          {username}
        </Typography>
        <Typography
          component={"p"}
          sx={{
            color: theme.palette.mainColor.text,
            fontSize: "20px",
            mt: "10px",
          }}
        >
          {profile.bio}
        </Typography>
        <div className="w-100 container d-flex justify-content-end">
          {user.username === profile.username ? (
            <Link
              className="btn rounded-pill border d-flex align-items-center"
              to="/settings"
              style={{
                color: theme.palette.mainColor.text,
                fontSize: "20px",
                mt: "10px",
                textDecoration: "none",
                width: "150px",
              }}
            >
              <IconButton>
                <SettingsIcon />
              </IconButton>
              Settings
            </Link>
          ) : (
            profile.following === false ?
            <div className="btn d-flex align-items-center rounded-pill border" >
              <IconButton onClick={clickhandeler} >
                <AddIcon />
                follow {username}
              </IconButton>
            </div>
            : 
            <div className="btn d-flex align-items-center rounded-pill border" >
            <IconButton onClick={clickhandeler} >
              <DoneIcon />
              followd {username}
            </IconButton>
          </div>
          )}
        </div>
      </Typography>
      


    </div>
  );
}
