import {Typography, useTheme } from "@mui/material";
import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import Tags from "../components/tags/Tags";
import Articles from "../components/articles/Articles";
import { getTagFetch } from "../ReduxPages/ReduxSlices/getArticlesByTag-slice";
import { articleFetch } from "../ReduxPages/ReduxSlices/getArticles-slice";
import PaginationComponent from "../components/pagination/PaginationReact";



export default function Home() {
  const theme = useTheme();
  const [tag, setTags] = useState('')
  const condition = useSelector((state) => state.condition);
  const getArticleByTag = useSelector((state) => state.getArticleByTag);
  const dispatch = useDispatch();
  const [apppear, setAppear] = useState(false);
  const articles = useSelector((state)=>state.article); 

  let numberOfArticles = articles.articlesCount;
    
  function tagsFilter(tag) {
    dispatch(getTagFetch(tag));
    setAppear(true);
    setTags(tag)
  }


  useEffect(()=>{
    setAppear(false);
    dispatch(articleFetch()); 
    
  },[])

  console.log(articles)

  function clickHandeler(){
    setAppear(false); 
  }

  return (
    <>
      {condition ? (
        ""
      ) : (
        <>
          <Typography
            component={"div"}
            sx={{
              width: "100%",
              height: "170px",
              backgroundColor: theme.palette.mainColor.main,
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
              flexDirection: "column",
            }}
            className="background"
          >
            <h2
              className="text-center"
              style={{ color: "#ffffff", fontSize: "30px", fontWeight: "bold" }}
            >
              conduit
            </h2>
            <p
              className="text-center"
              style={{ color: "#ffffff", fontSize: "20px" }}
            >
              {" "}
              A place to share Knowledge{" "}
            </p>
          </Typography>

          <div className="container mt-5">
            <div className="row">
              <div className="col-md-8">
                <button
                  className="border-bottom parabottom btn rounded-0"
                  style={{
                    color: theme.palette.mainColor.main,
                    position: "relative",
                    width:"180px"
                  }}
                  onClick={clickHandeler}
                >
                  Global Article   
                </button>
                {apppear ? <span style={{color: theme.palette.mainColor.main}}>
                {`#${tag}`}
                </span> :
                ''}
                <div className="border-bottom mb-3"></div>
                <Articles apppear={apppear} getArticleByTag={getArticleByTag} articleFetch={articleFetch}  articles ={articles} />
                <div>
                <PaginationComponent />
                </div>
              </div>
              <div className="col-md-4">
                <Tags tagsFilter={tagsFilter} />
              </div>
            </div>
          </div>
        </>
      )}
    </>
  );
}
