import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";

export const followFetch = createAsyncThunk('followSlice/followFetch', 
async({username,following ,token})=>{
    const res = await fetch(`https://api.realworld.io/api/profiles/${username}/follow`, 
    {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `token ${token}`
        },
        body: JSON.stringify({
            profile: {
            following,
          },
        }),
      })
    const data = await res.json(); 
    return data; 
}
)

const followSlice = createSlice({
    initialState:[], 
    name:'followSlice', 
    reducers:{}, 
    extraReducers:(builder)=>{
        builder.addCase(followFetch.fulfilled, (state, action)=>{
            return action.payload; 
        })
    }
})

export default followSlice.reducer;
