import {
  Avatar,
  Card,
  CardHeader,
  CardContent,
  Typography,
  IconButton,
  Button,
  useTheme,
} from "@mui/material";
import FavoriteIcon from "@mui/icons-material/Favorite";
import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { articleFetch } from "../../ReduxPages/ReduxSlices/getArticles-slice";

export default function Articles() {
  const theme = useTheme(); 
  const articles = useSelector((state) => state.article);
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(articleFetch());
  }, []);
  console.log(articles.articles);

  if(!Array.isArray(articles.articles)){
    return null
  }

  let articleMapping = articles.articles.map((article) => (
    <Card key={article.slug} sx={{ position: "relative", pb: "20px" }}>
      <CardHeader
        avatar={<Avatar src="" alt="m" />}
        title={article.author.username}
        subheader={article.createdAt}
      />
      <CardContent>
        <Typography component={"p"}>{article.title}</Typography>
        <Typography component={"p"} color="text.secondary">
          {article.body}
        </Typography>
      </CardContent>
      <div className="text-end me-5">{article.tagList.map((tag)=>
      <Button className="btn rounded-pill" sx={{color:theme.palette.mainColor.text, backgroundColor:theme.palette.mainColor.background}} key={tag}>
        {tag}
      </Button>
      )}</div>
      <IconButton
        aria-label="add to favorites"
        sx={{ position: "absolute", top: "10px", right: "10px" }}
      > 
        <span>{article.favoritesCount}</span>
        <FavoriteIcon />
      </IconButton>
    </Card>
  ));
  return (<>
  {articleMapping}
  </>);
}
