import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { Link, useNavigate } from "react-router-dom";
import { Box, Button, Container, TextField, Typography } from "@mui/material";
import { signUpFetch } from "../ReduxPages/ReduxSlices/signup-slice";
import { Formik, Field, Form } from "formik";
import * as Yup from "yup";

export default function SignUp() {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const validationSchema = Yup.object().shape({
    username: Yup.string()
      .required("Username is required")
      .min(4, "Username must be at least 4 characters")
      .max(20, "Username must be at most 20 characters")
      .matches(
        /^[a-zA-Z][a-zA-Z0-9-_]*$/,
        "username can contain alphanumeric characters, hyphens, and underscores"
      ),
    email: Yup.string().email("Invalid email").required("Email is required"),
    password: Yup.string()
      .required("Password is required")
      .min(8, "Password must be at least 8 characters")
      .max(20, "Password must be at most 20 characters")
      .matches(
        /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%]).{8,24}$/,
        "Password must contain at least one uppercase letter, one lowercase letter"
      ),
  });

  const initialValues = {
    username: "",
    email: "",
    password: "",
  };

  async function clickHandler(e) {
    e.preventDefault();
    await dispatch(signUpFetch({ username, email, password }));
    // navigate("/");
  }

  return (
    // <Container
    //   component={"div"}
    //   sx={{
    //     pt: "50px",
    //     pb: "50px",
    //     display: "flex",
    //     justifyContent: "center",
    //     alignItems: "center",
    //     flexDirection: "column",
    //   }}
    // >
    //   <Typography component={"h2"} sx={{ color: "#5CB85C", fontSize: "30px" }}>
    //     Sign Up
    //   </Typography>
    //   <Link to="/signin" className="haveAnAccount" style={{ color: "#5CB85C" }}>
    //     {" "}
    //     Have An Account{" "}
    //   </Link>
    //   <Box
    //     component={"form"}
    //     onSubmit={clickHandler}
    //     sx={{
    //       width: "70%",
    //       display: "flex",
    //       justifyContent: "center",
    //       alignItems: "center",
    //       flexDirection: "column",
    //     }}
    //   >
    //     <TextField
    //       id="username"
    //       label="Username"
    //       variant="outlined"
    //       sx={{ mt: "30px", mb: "30px", width: "70%" }}
    //       value={username}
    //       onChange={(e) => setUsername(e.target.value)}
    //     />
    //     <TextField
    //       id="email"
    //       label="Email"
    //       variant="outlined"
    //       sx={{ mb: "30px", width: "70%" }}
    //       value={email}
    //       onChange={(e) => setEmail(e.target.value)}
    //     />
    //     <TextField
    //       id="password"
    //       label="Password"
    //       variant="outlined"
    //       type="password"
    //       sx={{ mb: "30px", width: "70%" }}
    //       value={password}
    //       onChange={(e) => setPassword(e.target.value)}
    //     />
    //     <Button
    //       type="submit"
    //       className="btn"
    //       sx={{
    //         color: "#5CB85C",
    //         borderColor:"#5CB85C",
    //         borderWidth:"1px",
    //         borderStyle:"solid",
    //         "&:hover": { color: "white", backgroundColor: "#5CB85C" },
    //       }}
    //     >
    //       Register
    //     </Button>
    //   </Box>
    // </Container>
    <Formik
      initialValues={initialValues}
      validationSchema={validationSchema}
      onSubmit={clickHandler}
    >
      {({
        values,
        errors,
        touched,
        handleChange,
        handleBlur,
        handleSubmit,
        isSubmitting,
        /* and other goodies */
      }) => (
        <Form onSubmit={handleSubmit}>
          <Field
            as={TextField}
            type="text"
            name="username"
            id="username"
            label="Username"
            onChange={handleChange}
            onBlur={handleBlur}
            value={values.username}
            error={touched.username && !!errors.username}
            helperText={touched.username && errors.username}
          />
          <Field
            as={TextField}
            type="email"
            name="email"
            id="email"
            label="Email"
            onChange={handleChange}
            onBlur={handleBlur}
            value={values.email}
            error={touched.email && !!errors.email}
            helperText={touched.email && errors.email}
          />
          <Field
            as={TextField}
            type="password"
            name="password"
            id="password"
            label="Password"
            onChange={handleChange}
            onBlur={handleBlur}
            value={values.password}
            error={touched.password && !!errors.password}
            helperText={touched.password && errors.password}
          />
          <Button type="submit" disabled={isSubmitting}>
            Submit
          </Button>
        </Form>
      )}
    </Formik>
  );
}
