import { Button, Paper, useTheme } from "@mui/material";
import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { tagFetch } from "../../ReduxPages/ReduxSlices/tags-slice";
import { getTagFetch } from "../../ReduxPages/ReduxSlices/getArticlesByTag-slice";

export default function Tags(){
    const theme = useTheme();
    const getArticleByTag = useSelector((state)=>state.getArticleByTag);  
    const dispatch = useDispatch();
    useEffect(()=>{
        dispatch(tagFetch()); 
    },[]); 

    function clickHandeler(tag){
        dispatch(getTagFetch({tag}))
        console.log(getArticleByTag); 
        
    }

    const tags = useSelector((state)=>state.tags);
    if (!Array.isArray(tags)) {
        return null; 
    }
    
    let GettingTags = tags.map((tag)=>
        <Button onClick={(tag)=>clickHandeler(tag)} className="btn rounded-pill" sx={{backgroundColor:theme.palette.mainColor.background, color:theme.palette.mainColor.text, m:'5px'}} key={tag}>{tag}</Button>
    )
    return (
        <Paper sx={{p:'10px'}} elevation={3}>
            {GettingTags}
        </Paper>
    )
}
