import { Avatar, IconButton, Typography, useTheme } from "@mui/material";
import React from "react";
import { Link } from "react-router-dom";
import SettingsIcon from "@mui/icons-material/Settings";

export default function Profile() {
  const theme = useTheme();
  const user = JSON.parse(localStorage.getItem("user-info"));

  return (
    <div>
      <Typography
        component={"div"}
        className="background"
        sx={{
          width: "100%",
          height: "350px",
          backgroundColor: theme.palette.mainColor.main,
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          flexDirection: "column",
        }}
      >
        <Avatar
          sx={{ width: "100px", height: "100px" }}
          src={user.image}
          alt=""
        />
        <Typography
          component={"h2"}
          sx={{
            color: theme.palette.mainColor.text,
            fontSize: "30px",
            textTransform: "capitalize",
            mt: "10px",
          }}
        >
          {user.username}
        </Typography>
        <Typography
          component={"p"}
          sx={{
            color: theme.palette.mainColor.text,
            fontSize: "20px",
            mt: "10px",
          }}
        >
          {user.bio}
        </Typography>
        <div className="w-100">

        <Link
        className="btn rounded-pill border d-flex align-items-center flex-start"
          to="/settings"
          style={{
            color: theme.palette.mainColor.text,
            fontSize: "20px",
            mt: "10px",
            textDecoration: "none",
            width
          }}
        >
          <IconButton>
            <SettingsIcon />
          </IconButton>
          Settings
        </Link>
        </div>
      </Typography>
    </div>
  );
}
