import React, { useEffect, useState } from "react";
import Pagination from "@mui/material/Pagination";
import Stack from "@mui/material/Stack";
import { getArticlesByPageFetch } from "../../ReduxPages/ReduxSlices/getArticlesByPage-slice";
import { useDispatch } from "react-redux";

export default function PaginationComponent(props) {
 const [pageNumber, setPageNumber] = useState(1)
 
  let numberOfPages = Math.ceil(props.numberOfArticles / articlesPerPage);
  const dispatch = useDispatch(); 

    useEffect(()=>{
        dispatch(getArticlesByPageFetch(pageNumber));
    },[pageNumber, dispatch])
  
  const handlePageChange = (event, value) => {
    console.log("Current Page:", pageNumber);
    setPageNumber(value); 
  };
   

    return (
    <div className="container d-flex justify-content-center align-items-center pt-3 pb-3">
    <Stack spacing={0}>
      <Pagination count={numberOfPages}          
       page={pageNumber}
       onChange={handlePageChange} />
    </Stack>
    </div>
  );
}
