import { Container, Typography, Box, TextField, Button } from "@mui/material";
import { Field, Formik, Form } from "formik";
import React from "react";
import { Link } from "react-router-dom";
import * as yup from "yup";
import FormikTextField from "../components/FormikInputs/FormikTextField";

const registerSchema = yup.object().shape({
  username: yup
    .string()
    .max(20, "Username is too long (maximum is 20 characters)")
    .required("Username is required"),
  password: yup
    .string()
    .min(8, "Passwprd is too short (maximum is 8 characters)")
    .required("Password is required"),
  email: yup.string().email("Invalid email").required("Email is required"),
});

const handleFormSubmit = () => {}

export default function SignUp() {
  return (
    <Container
      component={"div"}
      sx={{
        pt: "50px",
        pb: "50px",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        flexDirection: "column",
      }}
    >
      <Typography component={"h2"} sx={{ color: "#5CB85C", fontSize: "30px" }}>
        Sign Up
      </Typography>
      <Link to="/signin" className="haveAnAccount" style={{ color: "#5CB85C" }}>
        {" "}
        Have An Account{" "}
      </Link>
      <Box
        component={"form"}
        sx={{
          width: "70%",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          flexDirection: "column",
        }}
      >
        {/* <TextField
          id="username"
          label="Username"
          variant="outlined"
          sx={{ mt: "30px", mb: "30px", width: "70%" }}
        />
        <TextField
          id="email"
          label="Email"
          variant="outlined"
          sx={{ mb: "30px", width: "70%" }}
        />
        <TextField
          id="password"
          label="Password"
          variant="outlined"
          sx={{ mb: "30px", width: "70%" }}
        />
        <Button
          type="submit"
          className="btn"
          sx={{
            color: "#5CB85C",
            "&:hover": { color: "white", backgroundColor: "#5CB85C" },
          }}
        >
          Register
        </Button> */}
        <Formik
          initialValues={{
            username: "",
            password: "",
            email: "",
          }}
          validationSchema={registerSchema}
          onSubmit={handleFormSubmit}
          render={() => (
            <Form>
                <FormikTextField name="username"></FormikTextField>
            </Form>
          ) }
        />
      </Box>
    </Container>
  );
}
