import { Avatar, CardHeader, Divider, useTheme } from "@mui/material";
import React, { useEffect } from "react";
import { condition } from "../ReduxPages/ReduxSlices/condition-slice";
import { Link, useParams } from "react-router-dom";
import { slugFetch } from "../ReduxPages/ReduxSlices/slug-slice";
import { useDispatch, useSelector } from "react-redux";

export default function SlugPage(){
    const theme = useTheme();
    const { slug } = useParams(); 
    const dispatch = useDispatch(); 
    useEffect(()=>{
        dispatch(slugFetch(slug))
    },[])
    const slugRedux = useSelector((state)=>state.slug);
    return(
        <>
        <div className="background" style={{backgroundColor:theme.palette.mainColor.main, width:'100%', height:'270px'}}>
            <div className="container">
                <h2> {slugRedux.description} </h2>
                <CardHeader 
                avatar={<Avatar src={slugRedux.author.image}  alt="m" />}
                title = {slugRedux.author.username}
                subheader={slugRedux.createdAt.slice(0, 10)}
                />
            </div>
        </div>
        <div className="container">
            <p style={{color:theme.palette.mainColor.text}}>{slugRedux.body}</p>
            <p>{article.tagList.map((tag) => (
          <Button
            className="btn rounded-pill ms-2"
            sx={{
              color: theme.palette.mainColor.text,
              backgroundColor: theme.palette.mainColor.background,
            }}
            key={tag}
          >
            {tag}
          </Button>
        ))}</p>
            <Divider />
            {
                condition ? '':
                <p>
                <Link to='/signin' style={{color:theme.palette.mainColor.main, textDecoration:'none'}} >
                <span>Sign In</span>
                </Link>
                or 
                <Link to='/signup' style={{color:theme.palette.mainColor.main, textDecoration:'none'}} >
                <span>Sign Up</span>
                </Link>
                to add comments on this article
                </p>
            }
        </div>
        </>

    )
}
