import React, { useEffect } from "react";
import {
  AppBar,
  Container,
  Toolbar,
  Typography,
  IconButton,
  Box,
  useTheme,
} from "@mui/material";
import MenuIcon from "@mui/icons-material/Menu";
import { Link, useLocation, useNavigate } from "react-router-dom";
export default function AppBarR(props) {
  const pages = ["Home", "Sign In", "sign Up"];
  const pagesHref = ["/", "/signin", "/signup"];
  const location = useLocation();
  const theme = useTheme();
//   const logInPages = ["Home", "New Post", "Settings"];
//   const logInPagesHref = ["/", "/newPost", `/settings`  ];
  const navigate = useNavigate();
  return (
    <AppBar
      position="static"
      sx={{ backgroundColor: "transparent", boxShadow: "none" }}
    >
      <Container>
        <Toolbar disableGutters>
          <IconButton
            aria-label=""
            sx={{ display: { xs: "flex", md: "none" } }}
            onClick={() => {
              props.openToggle();
              localStorage.setItem("current value", props.mode);
            }}
          >
            <MenuIcon />
          </IconButton>
          <Box sx={{ flexGrow: 1 }}>
            <Link
              to="/"
              style={{
                color: "#5CB85C",
                textDecoration: "none",
                fontSize: "25px",
              }}
            >
              Conduit
            </Link>
          </Box>
          <Box
            sx={{
              display: { xs: "none", md: "flex" },
              justifyContent: "center",
              alignItems: "center",
              flexDirection: "row",
            }}
          >
              {pages.map((page, index) => (
                  <Link
                    to={pagesHref[index]}
                    style={{
                      textDecoration: "none",
                      color: "#5CB85C",
                      backgroundColor:
                        location.pathname === pagesHref[index]
                          ? "#puple"
                          : "",
                    }}
                    key={page}
                  >
                    <Typography
                      className="btn"
                      component={"h2"}
                      variant="h6"
                      sx={{
                        fontSize: "16px",
                        p: "10px",
                      }}
                    >
                      {page}
                    </Typography>
                  </Link>
                ))}
          </Box>
        </Toolbar>
      </Container>
    </AppBar>
  );
}
